func TestMsgRaAuthenticateRoute(t *testing.T) {
	addr1 := sdk.AccAddress([]byte("from"))
	cert, err := os.ReadFile("../../testdata/attestation_cert_sw")
	require.NoError(t, err)
	// coins := sdk.NewCoins(sdk.NewInt64Coin("atom", 10))
	msg := RaAuthenticate{
		addr1,
		cert,
	}

	require.Equal(t, msg.Route(), RouterKey)
	require.Equal(t, msg.Type(), "node-auth")
}

func TestMsgSendValidation(t *testing.T) {
	_ = os.Setenv("SGX_MODE", "SW")

	addr0 := sdk.AccAddress([]byte("qwlnmxj7prpx8rysxm2u"))

	cert, err := os.ReadFile("../../testdata/attestation_cert_sw")
	require.NoError(t, err)

	certBadSig, err := os.ReadFile("../../testdata/attestation_cert_invalid")
	require.NoError(t, err)

	invalidCert := []byte("aaaaaaaaaaa")

	// var emptyAddr sdk.AccAddress

	cases := []struct {
		valid bool
		tx    RaAuthenticate
	}{
		{true, RaAuthenticate{
			addr0,
			cert,
		}},
		// invalid address send
		{false, RaAuthenticate{
			addr0,
			invalidCert,
		}}, // malformed certificate
		{false, RaAuthenticate{
			addr0,
			certBadSig,
		}}, // certificate with a bad signature
	}

	for _, tc := range cases {
		err := tc.tx.ValidateBasic()
		if tc.valid {
			require.Nil(t, err)
		} else {
			require.NotNil(t, err)
		}
	}
}

func TestMsgSendGetSignBytes(t *testing.T) {
	addr0 := sdk.AccAddress([]byte("qwlnmxj7prpx8rysxm2u"))

	cert, err := os.ReadFile("../../testdata/attestation_cert_sw")
	require.NoError(t, err)

	msg := RaAuthenticate{
		addr0,
		cert,
	}
	res := msg.GetSignBytes()
	expected := `{"type":"reg/authenticate","value":{"ra_cert":"","sender":"cosmos1w9mkcmnd0p4rwurjwpursunewdux6vn4d4tp6g"}}`
	require.Equal(t, expected, string(res))
}

